import React, {useEffect} from 'react';
import {useSelector, useDispatch} from "react-redux";
import {useNavigate, useParams} from "react-router-dom";
import {showSingleTask, reset} from "../features/tasks/taskSlice";
import HomeButton from "../components/HomeButton";
import SpinnerComponent from "../components/Spinner";
import {RootState} from "../app/store";
import {toast} from "react-toastify";
import taskItem from "../components/TaskItem";


const TaskShowPage = () => {

	const {task, isError, isSuccess, isLoading, message} = useSelector((state: RootState) => state.task);
	const dispatch = useDispatch();
	const navigate= useNavigate();

	const {taskId} = useParams();

	useEffect(() => {
		if (isError) {
			toast.error(message)
		}

		dispatch(showSingleTask(taskId!))
	}, [isError, message, taskId])

	return (
		<div>
			Task
		</div>
	);
};

export default TaskShowPage;
