import axios from "axios";
import {UserType} from "./authSlice";

const API_URI = '/api/users/register';

const register = async (user: UserType) => {
	const response = await axios.post(API_URI, user);

	console.log(response.data);

	if (response.data) {
		localStorage.setItem('user', JSON.stringify(response.data));
	}

	return response.data as Record<string, any> | string;
}

export const authService = {register}
